<?php//---------------------------用户自定义标签函数文件//软件结合项筛选选项function user_soft($ecms=0){	global $public_r,$navclassid;	//------- 函数参数设置开始 -----	//要显示的结合项字段列表，多个字段用半角逗号“,”隔开	$fieldandvar='type';	//字段显示选项设置，多项用双“#”号隔开，格式：选项说明|==|内容1,值1##内容2,值2	$fieldandval=array();	$fieldandval['type']='<ul class="tag_type_wrap mobile_game_icon clearfix ">|==|全部分类,##社交聊天,1##系统工具,2##金融理财,3##时尚购物,4##旅游出行,5##影音播放,6##生活实用,7##办公学习,8##资讯阅读,9##拍摄美化,10##医疗健康,11##其它软件,12';	//正常链接样式	$fieldandcss='fieldandcss';	//已选的选项链接样式	$changefieldandcss='current';	//字段与字段的显示间隔符，格式：开始显示字符|结束显示字符	$fieldexp='</ul>';	//选项与选项的显示间隔符，格式：开始显示字符|结束显示字符	$valexp='|';	//------- 函数参数设置结束 -----	$userfunecmsver=function_exists('ehtmlspecialchars')?1:0;	//附加参数	$urlcs='';	$mid=(int)$_GET['mid'];	if($mid)	{		$urlcs.='&mid='.$mid;	}	if($_GET['classid'])        {                $classid=RepPostVar($_GET['classid']);                $urlcs.=$classid;        }        else        {        $urlcs.=$navclassid;        }	if($_GET['ttid'])	{		$ttid=RepPostVar($_GET['ttid']);		$urlcs.='&ttid='.$ttid;	}	if($_GET['ztid'])	{		$ztid=RepPostVar($_GET['ztid']);		$urlcs.='&ztid='.$ztid;	}	if($_GET['firsttitle'])	{		$firsttitle=(int)$_GET['firsttitle'];		$urlcs.='&firsttitle='.$firsttitle;	}	if($_GET['isgood'])	{		$isgood=(int)$_GET['isgood'];		$urlcs.='&isgood='.$isgood;	}	if($_GET['endtime'])	{		$starttime=RepPostVar($_GET['starttime']);		$endtime=RepPostVar($_GET['endtime']);		$urlcs.='&starttime='.$starttime.'&endtime='.$endtime;	}	$line=(int)$_GET['line'];	if($line)	{		$urlcs.='&line='.$line;	}	$tempid=(int)$_GET['tempid'];	if($tempid)	{		$urlcs.='&tempid='.$tempid;	}	if($_GET['orderby'])	{		$orderby=RepPostVar($_GET['orderby']);		$myorder=(int)$_GET['myorder'];		$urlcs.='&orderby='.$orderby.'&myorder='.$myorder;	}	//间隔字符	$fieldexpr=explode('|',$fieldexp);	$valexpr=explode('|',$valexp);	//输出选项	$fr=explode(',',$fieldandvar);	$fcount=count($fr);	$allstr='';	$urladd='';	for($i=0;$i<$fcount;$i++)	{		$field=$fr[$i];		//选项链接		$getval='';		if($_GET[$field])		{			$getval=$userfunecmsver==1?ehtmlspecialchars($_GET[$field],ENT_QUOTES):htmlspecialchars($_GET[$field],ENT_QUOTES);			$urladd.='&'.$field.'='.urlencode($getval);		}		//选项说明		$vsayr=explode('|==|',$fieldandval[$field]);		//选项内容		$valallstr='';		$vr=explode('##',$vsayr[1]);		$vcount=count($vr);		for($vi=0;$vi<$vcount;$vi++)		{			$vtr=explode(',',$vr[$vi]);			if($vtr[1]=='' || $vtr[1]==null){$vtr[1]=0;}			if (preg_match("/[\x7f-\xff]/", $getval))				{					if(urlencode($getval)==urlencode($vtr[1]))						{							$css=$changefieldandcss;						}					else						{							$css=$fieldandcss;						}				}else{					if($getval==$vtr[1])						{							$css=$changefieldandcss;						}					else						{							$css=$fieldandcss;						}				}		$rjfl_value=news_value('type',$i,0,$vtr[1]);		// 以上每个加一个字段都需定义		$valallstr.=$valexpr[0].'<li class="'.$css.'"><a href="'.$public_r['newsurl'].'soft/'.$rjfl_value.'0-0.html"><i class="icon"></i>'.$vtr[0].'</a></li>'.$valexpr[1];		}		$allstr.=$vsayr[0].$valallstr.$fieldexpr[1].$fieldexpr[0];	}	$allstr=str_replace('<!--url.add-->',$urladd,$allstr);	echo $allstr;}//游戏结合项筛选选项function user_game($ecms=0){	global $public_r,$navclassid;	//------- 函数参数设置开始 -----	//要显示的结合项字段列表，多个字段用半角逗号“,”隔开	$fieldandvar='type';	//字段显示选项设置，多项用双“#”号隔开，格式：选项说明|==|内容1,值1##内容2,值2	$fieldandval=array();    //	$fieldandval['type']='<ul class="tag_type_wrap mobile_game_icon clearfix ">|==|全部分类,##角色扮演,1##动作格斗,2##休闲益智,3##枪战射击,4##战争策略,5##卡牌对战,6##音乐舞蹈,7##模拟经营,8##体育竞技,9##冒险解谜,10##其它游戏,11';	//正常链接样式	$fieldandcss='fieldandcss';	//已选的选项链接样式	$changefieldandcss='current';	//字段与字段的显示间隔符，格式：开始显示字符|结束显示字符	$fieldexp='</ul>';	//选项与选项的显示间隔符，格式：开始显示